import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext'; // AuthContext yolunuzu doƒürulayƒ±n
import toast from 'react-hot-toast';

// Basit ƒ∞konlar (SVG)
const SearchIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
    <path strokeLinecap="round" strokeLinejoin="round" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
  </svg>
);

const UserCircleIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-7 w-7 text-gray-600 hover:text-red-600" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
    <path strokeLinecap="round" strokeLinejoin="round" d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
  </svg>
);


export default function Header() {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');

  const handleLogout = async () => {
    try {
      await logout();
      toast.success('Ba≈üarƒ±yla √ßƒ±kƒ±≈ü yapƒ±ldƒ±!');
      navigate('/login');
    } catch (error) {
      toast.error('√áƒ±kƒ±≈ü yapƒ±lamadƒ±.');
      console.error("√áƒ±kƒ±≈ü hatasƒ±:", error);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      // Arama sonu√ßlarƒ± sayfasƒ±na y√∂nlendirme veya arama i≈ülemini tetikleme
      // navigate(`/search?q=${encodeURIComponent(searchTerm)}`);
      toast(`Arama: ${searchTerm}`, { icon: 'üîç' });
      setSearchTerm(''); // Arama sonrasƒ± inputu temizle
    }
  };

  return (
    <header className="bg-white shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Sol Taraf: Logo/Ba≈ülƒ±k */}
          <div className="flex-shrink-0">
            <Link to="/" className="text-2xl font-bold text-red-600 hover:text-red-700">
              Book Advisor
            </Link>
          </div>

          {/* Orta: Arama √áubuƒüu */}
          <div className="flex-grow max-w-xl mx-4">
            <form onSubmit={handleSearch} className="relative">
              <input
                type="search"
                name="search"
                id="search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Kitap, yazar veya t√ºr ara..."
                className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-red-500 focus:border-red-500 sm:text-sm"
              />
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <SearchIcon />
              </div>
            </form>
          </div>

          {/* Saƒü Taraf: Kullanƒ±cƒ± Bilgileri ve √áƒ±kƒ±≈ü */}
          <div className="flex items-center space-x-4">
            {currentUser ? (
              <>
                <span className="text-sm text-gray-600 hidden md:block">{currentUser.email}</span>
                <Link to="/account" title="Hesabƒ±m">
                  <UserCircleIcon />
                </Link>
                <button
                  onClick={handleLogout}
                  className="px-3 py-2 text-sm font-medium text-white bg-red-500 rounded-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-400"
                >
                  √áƒ±kƒ±≈ü Yap
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="text-sm font-medium text-gray-600 hover:text-red-600">
                  Giri≈ü Yap
                </Link>
                <Link
                  to="/register"
                  className="px-3 py-2 text-sm font-medium text-white bg-red-500 rounded-md hover:bg-red-600"
                >
                  Kayƒ±t Ol
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </header>
  );
}
