import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import Header from '../components/Header';
import toast from 'react-hot-toast';

// Sidebar icons
const HomeIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 inline" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
  </svg>
);
const CategoryIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 inline" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h7" />
  </svg>
);
const AccountIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 inline" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
  </svg>
);
const AdminIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 inline" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M11 12h2m-1-1v2m8 4h.01M4 4l16 16" />
  </svg>
);
const LogoutIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 inline" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
  </svg>
);

export default function HomePage() {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      toast.success('Başarıyla çıkış yapıldı!');
      navigate('/login');
    } catch (error) {
      toast.error('Çıkış yapılamadı.');
      console.error('Logout error:', error);
    }
  };

  // Define links with role-based access
  const allLinks = [
    { name: 'Anasayfa', path: '/', icon: <HomeIcon />, roles: ['member', 'admin'] },
    { name: 'Kategoriler', path: '/categories', icon: <CategoryIcon />, roles: ['member', 'admin'] },
    { name: 'Hesap', path: '/account', icon: <AccountIcon />, roles: ['member', 'admin'] },
    { name: 'Admin Panel', path: '/admin/books', icon: <AdminIcon />, roles: ['admin'] },
  ];

  // Filter by current user's role
  const sidebarLinks = currentUser ? allLinks.filter(link => link.roles.includes(currentUser.role)) : [];

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      <Header />
      <div className="flex flex-1">
        <aside className="w-64 bg-white p-4 shadow-lg space-y-2 hidden md:block">
          <nav>
            {sidebarLinks.map(link => (
              <button
                key={link.name}
                onClick={() => navigate(link.path)}
                className="w-full text-left flex items-center px-3 py-2.5 text-gray-700 hover:bg-red-50 hover:text-red-600 rounded-md transition duration-150"
              >
                {link.icon}
                {link.name}
              </button>
            ))}
            {currentUser && (
              <button
                onClick={handleLogout}
                className="w-full flex items-center mt-4 px-3 py-2.5 text-gray-700 hover:bg-red-50 hover:text-red-600 rounded-md transition duration-150"
              >
                <LogoutIcon />
                Çıkış Yap
              </button>
            )}
          </nav>
        </aside>

        <main className="flex-1 p-6">
          <h1 className="text-3xl font-semibold text-gray-800 mb-6">Anasayfa</h1>
          {/* Diğer içerikler burada */}
        </main>
      </div>
    </div>
  );
}


