                  path="/"
                  element={
                    <PrivateRoute>
                      <Home /> {/* Home (HomePage) artÄ±k kendi layout'unu ve header'Ä±nÄ± yÃ¶netecek */}
                    </PrivateRoute>
                  }
                />
                {/* Admin routes */}
                <Route
                  path="/admin/books"
                  element={
                    <PrivateRoute requiredRole="admin">
                      {/* AdminBooks da kendi Header'Ä±nÄ± veya ortak bir Layout'u kullanabilir */}
                      <AdminBooks />
                    </PrivateRoute>
                  }
                />
                {/* Member routes */}
