import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { AuthProvider, useAuth } from './contexts/AuthContext';

// Sayfalar
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';
import AdminBooks from './pages/AdminBooks';
import CategoriesPage from './pages/CategoriesPage';
import AccountPage from './pages/AccountPage';

// Gerekirse özel yetkisiz sayfası
import UnauthorizedPage from './pages/UnauthorizedPage'; // Opsiyonel

// Kullanıcı girişi ve rol kontrolü yapan bileşen
function PrivateRoute({ children, requiredRole }) {
  const { currentUser, loading } = useAuth();

  if (loading) {
    return <div className="text-center py-10 text-lg font-semibold">Yükleniyor...</div>;
  }

  if (!currentUser) {
    return <Navigate to="/login" replace />;
  }

  if (requiredRole && currentUser.role !== requiredRole) {
    return <Navigate to="/unauthorized" replace />; // veya anasayfaya yönlendir: "/"
  }

  return children;
}

function App() {
  return (
    <Router>
      <AuthProvider>
        <Toaster position="top-right" />
        <div className="min-h-screen bg-gray-100">
          <main>
            <Routes>
              {/* Public Routes */}
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />

              {/* Protected Routes */}
              <Route
                path="/"
                element={
                  <PrivateRoute>
                    <Home />
                  </PrivateRoute>
                }
              />
              <Route
                path="/categories"
                element={
                  <PrivateRoute>
                    <CategoriesPage />
                  </PrivateRoute>
                }
              />
              <Route
                path="/account"
                element={
                  <PrivateRoute>
                    <AccountPage />
                  </PrivateRoute>
                }
              />

              {/* Admin Routes */}
              <Route
                path="/admin/books"
                element={
                  <PrivateRoute requiredRole="admin">
                    <AdminBooks />
                  </PrivateRoute>
                }
              />

              {/* Optional: Unauthorized Route */}
              <Route path="/unauthorized" element={<UnauthorizedPage />} />

              {/* Catch-all (404 Redirect to Home) */}
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </main>
        </div>
      </AuthProvider>
    </Router>
  );
}

export default App;
